module.exports = remove
const Discord = require('discord.js')
function remove (oldmessage, botmessage, only = true) {
    botmessage.react("ğŸ—‘");
    var reactedFilter = ""
  if(only = true) {
  reactedFilter = (reaction, user) => reaction.emoji.name === 'ğŸ—‘' && user.id === oldmessage.author.id;
  } else {
  reactedFilter = (reaction, user) => reaction.emoji.name === 'ğŸ—‘' && !user.bot
  }
  const reacted = botmessage.createReactionCollector(reactedFilter);
  reacted.on('collect', r => {
    botmessage.delete();
    botmessage.channel.send('Deleted.')
      .then(message => message.delete(5000))
})
}